import React, { useState } from 'react';
import { ColorPicker, useColor } from "react-color-palette";
import "./react-color-palette/lib/css/styles.css";
import './PaletteGenerator.css';

function PaletteGenerator() {
    const [startColor, setStartColor] = useColor("hex", "#ffffff");
    const [endColor, setEndColor] = useColor("hex", "#000000");
    const [palette, setPalette] = useState([]);

    const generatePalette = () => {
        // Logic to generate intermediary colors goes here
        // This is placeholder logic. You would generate a range of colors
        // between startColor and endColor.
        const generatedPalette = [startColor.hex, endColor.hex]; // Replace with actual generated colors
        setPalette(generatedPalette);
    };

    return (
        <div className="PaletteGenerator">
            <div className="palette-controls">
                <div>
                    <p>Start Color:</p>
                    <ColorPicker 
                        width={456}
                        height={228}
                        color={startColor}
                        onChange={setStartColor}
                        hideHSV
                        dark
                    />
                </div>
                <div>
                    <p>End Color:</p>
                    <ColorPicker 
                        width={456}
                        height={228}
                        color={endColor}
                        onChange={setEndColor}
                        hideHSV
                        dark
                    />
                </div>
                <button onClick={generatePalette}>Generate Palette</button>
            </div>
            <div className="palette-display">
                {palette.length > 0 && palette.map((color, index) => (
                    <div key={index} className="color-block" style={{ backgroundColor: color }}>
                        {color}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default PaletteGenerator;
